    private void listaColoresActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_listaColoresActionPerformed
        // TODO add your handling code here:
        if (listaColores.getSelectedItem()==0){
            this.textArea.append("No se ha seleccionado ningun elemento de la lista desplegable es \n");
        } else {
            this.textArea.append("El elemento seleccionado de la lista desplegable es \n");
            this.textArea.append("indice:"+  listaColores.getSelectedIndex() + " - color:"+ listaColores.getSelectedItem()+ "\n");
        }
    }//GEN-LAST:event_listaColoresActionPerformed

