    private void bDialogoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bDialogoActionPerformed
        // TODO add your handling code here:
        String val = JOptionPane.showInputDialog(this, "¿Cuantos dedeos ves aqui?", "Caja Diálogo", JOptionPane.QUESTION_MESSAGE);
        if (val!=null&&!val.equals("")){
            this.textArea.append("Has introducido el valor" + val + "en el cuadro de dialogo");
        }else{
            this.textArea.append("No has introducido valor en el cuadro de dialogo");
        }
        
    }//GEN-LAST:event_bDialogoActionPerformed

    private void LAFBasicActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LAFBasicActionPerformed
        // TODO add your handling code here:
        try {
            UIManager.setLookAndFeel("javax.swing.plaf.basic.BasicLookAndFeel");
            SwingUtilities.updateComponentTreeUI(this);
        } catch (Exception e) {
            this.textArea.append(e.toString());
        }
    }//GEN-LAST:event_LAFBasicActionPerformed

    private void LAFInstalledActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LAFInstalledActionPerformed
        // TODO add your handling code here:
        UIManager.LookAndFeelInfo[] lafs = UIManager.getInstalledLookAndFeels();
        String[] columnas = {"Nombre","Clase"};
        Object[][] datosLafs = new Object [5][2];
        int i=0;
        
        for (UIManager.LookAndFeelInfo laf: lafs){
            datosLafs[i][0] = laf.getName();
            datosLafs[i][1] = laf.getClassName();
            i++;
        }
        
        JTable tabla = new JTable (datosLafs,columnas);
        
        /*Indica el tamaño de las columnas a la hora de mostrarlas*/
        tabla.getColumnModel().getColumn(0).setPreferredWidth(120);
        tabla.getColumnModel().getColumn(1).setPreferredWidth(380);
        /*Se se pone directamente la tabla de JOptionPane, no muestra los ordenes de la columnas por lo que optamos por
         *lo que optamos por ponerlo en JScroll Panel*/
        JScrollPane panel = new JScrollPane(tabla);
        panel.setPreferredSize(new Dimension (500, 110));
        
        JOptionPane.showMessageDialog(this, panel, "Tipos de Look and Feel Instalados", JOptionPane.INFORMATION_MESSAGE);
        
        
    }//GEN-LAST:event_LAFInstalledActionPerformed

