    private void listaNombresMouseExited(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_listaNombresMouseExited
        // TODO add your handling code here:
        List <String> items = this.listaNombres.getSelectedValuesList();
        if (items.size()==0){
            this.textArea.append("No se ha seleccionado ningun elemento de la lista de seleccion multiple de nombres\n");
        }else{
            this.textArea.append("Los elementos seleccionados de la lista de seleccion multiple de nombres son\n");
            for (int i=0;i<items.size(); i++){
                this.textArea.append("\t"+ (items).get(i) + "\n");
            }
        }
    }//GEN-LAST:event_listaNombresMouseExited

